<?phpnamespace Netliva\SymfonyFormBundle\Form\Types;use Symfony\Component\Form\Extension\Core\Type\ButtonType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\Form\FormInterface;use Symfony\Component\Form\FormView;use Symfony\Component\Form\AbstractType;use Symfony\Component\OptionsResolver\OptionsResolver;class NetlivaAjaxSubmitType extends AbstractType{	public function buildForm(FormBuilderInterface $builder, array $options): void	{		$builder->setMapped(false);	}	public function getParent(): string	{		return ButtonType::class;	}	public function buildView (FormView $view, FormInterface $form, array $options): void	{	}	public function configureOptions(OptionsResolver $resolver): void	{		$resolver->setDefaults([			'constraints'	=> [],		]);	}	public function getBlockPrefix(): string	{		return 'netliva_ajax_submit';	}}