<?phpnamespace Netliva\SymfonyFormBundle\Form\Types;use Symfony\Component\Form\Extension\Core\Type\ButtonType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\Form\FormInterface;use Symfony\Component\Form\FormView;use Symfony\Component\Form\AbstractType;use Symfony\Component\OptionsResolver\OptionsResolver;use Symfony\Component\OptionsResolver\OptionsResolverInterface;class NetlivaAjaxSubmitType extends AbstractType{	public function buildForm (FormBuilderInterface $builder, array $options)	{		$builder->setMapped(false);	}	public function getParent ()	{		return ButtonType::class;	}	public function buildView (FormView $view, FormInterface $form, array $options)	{	}	public function configureOptions (OptionsResolver $resolver)	{		$resolver->setDefaults([			'constraints'	=> [],		]);	}	public function getBlockPrefix()	{		return 'netliva_ajax_submit';	}}