<?phpnamespace Netliva\SymfonyFormBundle\Form\Types;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\OptionsResolver\OptionsResolver;class NetlivaColorPickerType extends AbstractType{    public function configureOptions(OptionsResolver $resolver): void    {        $resolver->setDefaults(array(			'widget'   => 'single_text',            'compound' => false,        ));    }    public function getBlockPrefix(): string    {        return 'netliva_color_picker';    }    public function getParent()    {        return TextType::class;    }}