<?phpnamespace Netliva\SymfonyFormBundle\Form\Types;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\Extension\Core\Type\TextType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\Form\FormEvent;use Symfony\Component\Form\FormEvents;use Symfony\Component\Form\FormView;use Symfony\Component\Form\FormInterface;use Symfony\Component\OptionsResolver\OptionsResolver;class NetlivaIconType extends AbstractType{    public function configureOptions(OptionsResolver $resolver): void    {        $resolver->setDefaults(array(			'widget' => 'single_text',            'compound' => false,        ));    }    public function getBlockPrefix(): string    {        return 'netliva_icon';    }    public function getParent(): ?string    {        return TextType::class;    }}